export interface DbCol{
   table_catalog: string, 
   table_schema: string, 
   table_name: string, 
   column_name: string,
   is_identity: 'YES' | 'NO'
   is_nullable: 'YES' | 'NO'
   data_type: 'bigint' | 'character varying'
   ordinal_position: number
   udt_name: string
   column_default: any
}

export interface IFk{
   table_schema: string
   constraint_name: string
   table_name: string
   column_name: string
   foreign_table_schema: string
   foreign_table_name: string
   foreign_column_name: string
}

export class Col{
   table_catalog: string
   table_schema: string
   table_name: string
   column_name: string
   is_primary_key: boolean
   is_nullable: boolean
   ordinal_position: number
   column_default: any
   data_type: 'number' | 'varchar' | 'date' | 'fk' | 'm2m' | 'id'
   fk?: Fk
}

export class Fk {
   foreign_table_schema: string
   foreign_table_name: string
   foreign_column_name: string
   foreign_title_column_name: string
}
