import { Col } from "common/col"

export interface IApiResult<T>{
   statusCode: number
   message: string
   result: T
}

export interface ITableApi{
   cols() : IApiResult<Col[]>
   beans(args: {fields?: string[]}) : IApiResult<any[]>
   bean(args: {id: string, fields?: string[]}) : IApiResult<any>
   save(args: {bean: any}) : IApiResult<any>
   remove(args: {ids: string[]}) : IApiResult<string[]>
}